      console.log('🔄 Настройка синхронизации скролла...');

      if (!timeHeader || !mainArea || !basement) {
        console.log('❌ Не все контейнеры найдены:', {
          timeHeader: !!timeHeader, 
          mainArea: !!mainArea,
          basement: !!basement
        });
        return;
      }

      let isScrolling = false;

      // Основная диаграмма - главный источник скролла
      const onMainScroll = () => {
        if (isScrolling) return;
        isScrolling = true;

        const scrollLeft = mainArea.scrollLeft;

        // Синхронизируем заголовок и подвал
        if (timeHeader.scrollLeft !== scrollLeft) {
          timeHeader.scrollLeft = scrollLeft;
        }
        if (basement.scrollLeft !== scrollLeft) {
