      // Добавляем только один слушатель - на основную диаграмму
      mainArea.addEventListener('scroll', onMainScroll, { passive: true });

      console.log('✅ Синхронизация горизонтального скролла настроена');

      return () => {
        mainArea.removeEventListener('scroll', onMainScroll);
      };
    }, 500);

    return () => clearTimeout(timer);
  }, []);

