      });
    };

    const onMainVerticalScroll = () => {
      if (!isVerticalScrolling) {
        syncScrollTop(mainArea.scrollTop, 'main');
      }
    };

    const onDriversVerticalScroll = () => {
      if (!isVerticalScrolling) {
        syncScrollTop(driversArea.scrollTop, 'drivers');
      }
