  // Группируем рейсы по машинам
  const flightsByMachine = useMemo(() => {
    const byMachine: Record<string, Flight[]> = {};
    machines.forEach(m => byMachine[m.id] = []);

    flights.forEach(f => {
      if (f.vehicleId && f.vehicleId.trim() !== '') {
